sys_script_include.script:
var cs_cds_utils_ajax = Class.create();
cs_cds_utils_ajax.prototype = Object.extendsObject(AbstractAjaxProcessor, {
	showCDSLogsRelatedList: function() {
		var taskId = this.getParameter('sysparm_task_id');
		var grTask = new GlideRecord('sc_task');
		grTask.get(taskId);
		var cat_item = grTask.request_item.cat_item.sys_id + '';
		var isAdmin = gs.hasRole('admin');
		return (cat_item == '579a1fbd4f735300c53eca1f0310c701' && isAdmin) ? true : false;
	},

	type: 'cs_cds_utils_ajax'
});
